(479, 40, 'en', 'Sales Invoice Create', '<p><span style=\"font-size: 12pt;\"><strong>Dear</strong>&nbsp;{invoice_client}</span><span style=\"font-size: 12pt;\">,</span></p>\n                            <p><span style=\"font-size: 12pt;\">We have prepared the following invoice for you :#{invoice_id}</span></p>\n                            <p><span style=\"font-size: 12pt;\"><strong>Invoice Status</strong> : {invoice_status}</span></p>\n                            <p>Please Contact us for more information.</p>\n                            <p><span style=\"font-size: 12pt;\">&nbsp;</span></p>\n                            <p><strong>Kind Regards</strong>,<br /><span style=\"font-size: 12pt;\">{app_name}</span></p>', NULL, '{\n                    \"Sales Invoice Number\": \"invoice_id\",\n                    \"Sales Invoice Client\": \"invoice_client\",\n                    \"Sales Invoice Status\": \"invoice_status\",\n                    \"Sales Invoice Total\": \"invoice_sub_total\",\n                    \"Sales Invoice Issue Date\": \"created_at\",\n                    \"App Url\": \"app_url\",\n                    \"App Name\": \"app_name\"\n                  }', '2024-12-07 09:22:44', '2024-12-07 09:22:44'),
(480, 40, 'es', 'Sales Invoice Create', '<p><b>Querida</b><span style=\"font-size: 12pt;\">&nbsp;{invoice_client}</span><span style=\"font-size: 12pt;\">,</span></p><p>Hemos preparado la siguiente factura para ti<span style=\"font-size: 12pt;\">:&nbsp;&nbsp;{invoice_id}</span></p><p><b>Estado de la factura</b><span style=\"font-size: 12pt;\">: {invoice_status}</span></p><p>Por favor contctenos para ms informacin<span style=\"font-size: 12pt;\">.</span></p><p><b>Saludos cordiales</b><span style=\"font-size: 12pt;\">,<br></span>{app_name}</p>', NULL, '{\n                    \"Sales Invoice Number\": \"invoice_id\",\n                    \"Sales Invoice Client\": \"invoice_client\",\n                    \"Sales Invoice Status\": \"invoice_status\",\n                    \"Sales Invoice Total\": \"invoice_sub_total\",\n                    \"Sales Invoice Issue Date\": \"created_at\",\n                    \"App Url\": \"app_url\",\n                    \"App Name\": \"app_name\"\n                  }', '2024-12-07 09:22:44', '2024-12-07 09:22:44'),
